sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var utils = new GlideAjax('RPDraftUtilsClient');
	utils.addParam('sysparm_name', 'hasVariables');
	utils.addParam('sysparm_item', newValue);
	utils.getXMLAnswer(ajaxResponse);

	function ajaxResponse(response) {
		var resObject = JSON.parse(response);

		if(resObject.hasVariables == false) {
			g_form.addErrorMessage('Item "' + resObject.name + '" can\'t be enrolled as it has no variables.');
			g_form.setValue('item', '');
		}

		if(resObject.isEnrolled) {
			g_form.addErrorMessage('Item "' + resObject.name + '" is already enrolled!');
			g_form.setValue('item', '');
		} 
	}   
}
