sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var ga = new GlideAjax('RPDraftUtilsClient');
	ga.addParam('sysparm_name', 'getClass');
	ga.addParam('sysparm_item', newValue);
	ga.getXMLAnswer(ajaxResponse);

	function ajaxResponse(response) {

		switch(response) {
			case 'sc_cat_item_producer':
				g_form.setVisible('redirection_page', true);
				break;

			case 'sc_cat_item_guide':
				g_form.setValue('multiple_item_enrollment', true);
				g_form.setVisible('multiple_item_enrollment', false);
				g_form.setValue()//add another GA here to gather all related cat items and add them to the list collector;
		}

		if(response == 'sc_cat_item_producer') {
			g_form.setVisible('redirection_page', true);
		} else {
			g_form.setVisible('redirection_page', false);
		}
	}
}
